function one(){
    'use strict';
let x = parseFloat(document.getElementById('one').value);
let y = two(x);
    document.getElementById('output').innerHTML = "array included: " + y;
}


function two(x){
    let y = 0;
	let z = [];
    for (let i = 0; i < 10; i++) {
        y += i * x;
		z [i] = y;
    }
arr(z);
return z;
}

function arr(z){
    if(!z){
        document.getElementById('output').innerHTML = "empty";
    }
}